import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import MyPage from './MyPage';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    title: '',
    location: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const { name, title, location, email } = formData;

    alert(
      `예약이 완료되었습니다!\n\n` +
      `이름: ${name}\n` +
      `일정 제목: ${title}\n` +
      `위치: ${location}\n` +
      `이메일: ${email}`
    );
  };

  return (
    <div>
      <Header />
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <label>이름</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="홍길동"
            required
          />

          <label>일정 제목</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="감성 카페 투어 가자!"
            required
          />

          <label>일정 위치</label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            placeholder="동명동"
            required
          />

          <label>이메일</label>
          <textarea
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="example@email.com"
            required
          />

          <button className="next-button" type="submit">다음</button>
        </form>
      </div>
    </div>
  );
}

export default App;
