import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Home from './Home';
import MyPage from './MyPage';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    name: '',
    title: '',
    location: '',
    email: '',
  });

  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setShowModal(true);
  };

  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={
          <div className="form-container">
            <form onSubmit={handleSubmit}>
              <label>이름</label>
              <input name="name" value={formData.name} onChange={handleChange} placeholder="홍길동" required />

              <label>일정 제목</label>
              <input name="title" value={formData.title} onChange={handleChange} placeholder="경성 카페 투어 가자!" required />

              <label>일정 위치</label>
              <input name="location" value={formData.location} onChange={handleChange} placeholder="동명동" required />

              <label>이메일</label>
              <textarea name="email" value={formData.email} onChange={handleChange} placeholder="example@email.com" required />

              <button type="submit" className="next-button">다음</button>
            </form>

            {/* 모달 */}
            {showModal && (
              <div className="modal-overlay">
                <div className="modal-box">
                  <h2>예약이 완료되었습니다!</h2>
                  <p><strong>이름:</strong> {formData.name}</p>
                  <p><strong>일정 제목:</strong> {formData.title}</p>
                  <p><strong>위치:</strong> {formData.location}</p>
                  <p><strong>이메일:</strong> {formData.email}</p>
                  <button onClick={() => setShowModal(false)}>확인</button>
                </div>
              </div>
            )}
          </div>
        } />
        <Route path="/mypage" element={<MyPage />} />
      </Routes>
    </Router>
  );
}

export default App;
